$(document).ready(function(){var a=$(".js-newslist");if(window.location.hash){var t=window.location.hash.split("#")[1],n=a.find('[data-filter="'+t+'"]')[0];t&&a.find(".panel").show().not('[data-section="'+t+'"]').hide(),$(n).removeClass("btn-default").addClass("btn-primary").siblings(".btn-primary").removeClass("btn-primary").addClass("btn-default")}a.find("[data-filter]").on("click",function(t){t.preventDefault();var n=$(this).data("filter");n&&(a.find(".panel").show().not('[data-section="'+n+'"]').hide(),window.location.hash=n),$(this).removeClass("btn-default").addClass("btn-primary").siblings(".btn-primary").removeClass("btn-primary").addClass("btn-default")})});